    }

    public static function getOriName($name)
    {
        if (!empty(self::$alias[$name]))
        {
            return self::$alias[$name];
        }
        return $name;
    }

    public static function isAliasOk($alias)
    {
        $_tmpArr = array_flip($alias);
        if (isset($_tmpArr[$alias]))
        {
            return false;
        }
        return true;
    }
